function openOverlay(e){var t=e.target,r=t.parentElement;if(t.classList.contains("video-overlay-trigger")||r.classList.contains("video-overlay-trigger")){var o=t.getAttribute("data-src")||r.getAttribute("data-src"),a=document.querySelector(".video-overlay.hidden");a&&(a.querySelector("iframe").src=o,a.classList.remove("hidden"))}}function closeOverlay(){var e=document.querySelector(".video-overlay");e&&(e.classList.add("hidden"),e.querySelector("iframe").src="")}window.addEventListener("DOMContentLoaded",function(){document.querySelector(".videos").addEventListener("click",openOverlay,{passive:!0}),document.querySelector(".video-overlay-close").addEventListener("click",closeOverlay,{passive:!0})});